mapper.select:
SELECT table_name as Name,Engine,Version,Row_format,table_rows,Avg_row_length, Data_length,Max_data_length,Index_length,Data_free,Auto_increment, Create_time,Update_time,Check_time,table_collation,Checksum, Create_options,table_comment as Comment FROM information_schema.tables WHERE table_schema = DATABASE()
and (table_name like concat('%',#{condition},'%') or table_comment like concat('%',#{condition},'%'))
LIMIT #{startNum},#{recordsPerPage}
